CREATE FUNCTION action.usr_visible_holds(usr_id integer) RETURNS SETOF action.hold_request
    LANGUAGE plpgsql
    AS $$
DECLARE
    h               action.hold_request%ROWTYPE;
    view_age        INTERVAL;
    view_count      INT;
    usr_view_count  actor.usr_setting%ROWTYPE;
    usr_view_age    actor.usr_setting%ROWTYPE;
