        AND acsaf.main_entry IS NULL
        AND NOT are.deleted
        AND cmf.field_class = search_class
    ORDER BY
        -- favor 'lcsh' headings over other thesauri,
        -- falling back to record size as a tie-breaker
        CASE
            WHEN ash.thesaurus = 'lcsh' THEN 1000000
            ELSE LENGTH(are.marc)
        END
    DESC
    LIMIT 1;
$$;


